import React, { useState } from 'react'
import './Banner.css'
import {useEffect} from 'react'
import axios from '../../axios'
import {API_KEY} from '../../constants/constants'

export default function Banner() {
    const [movie,setMovie]= useState()
    useEffect(() => {
        axios.get(`trending/all/week?api_key=${API_KEY}&language=en-US`).then((Response)=>
       {
           console.log(Response.data.results[0]);
           setMovie(Response.data.results[0])
       } )
        
    }, [])
    return (
        <div className="banner">
            <div className="content">
                <h1 className="title">{movie ? movie.original_name : ""}</h1>
                <div className="bannerbuttons">
                    <button className="button">Play</button>
                    <button className="button">My List</button>
                </div>
                <div>
                    <h1  className="description">Ein Projekt startet und doch es gibt noch keinen Text, allerdings sollte das Layout schon bald präsentiert werden ... was tun?
                    Damit das Projekt gleich starten kann benutze einfach etwas Lorem ipsum; Blind-, Füll-, Dummy-, Nachahmungs-, Platzhaltertext.</h1>
                </div>
            </div>
            <div className="fadebottom"></div>
        </div>
    )
}
